const convertToCelsius = function(f) {
  return c = ((Math.round(
  ((f-32)*5/9)*10)) / 10)
};

const convertToFahrenheit = function(a) {
   return b = ((Math.round(
  (a/5*9+32)*10)
             )/10)
};

